// HistoryPage.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const HistoryPage = () => {
  const [pastCampaigns, setPastCampaigns] = useState([]);

  useEffect(() => {
    fetchPastCampaigns();
  }, []);

  const fetchPastCampaigns = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/campaigns/past');
      setPastCampaigns(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="history-container">
      <h2>Past Campaigns</h2>
      {pastCampaigns.length > 0 ? (
        <ul className="history-list">
          {pastCampaigns.map(campaign => (
            <li key={campaign.id}>
              Campaign ID: {campaign.id}, Created At: {campaign.created_at}, Status: {campaign.status}
            </li>
          ))}
        </ul>
      ) : (
        <p>No past campaigns found</p>
      )}
    </div>
  );
};

export default HistoryPage;
